import React, { useState, useEffect, useRef } from "react";
import ReactDOM from 'react-dom';
import Move from "./Move";

export default function Board(props) {
  const [player, setPlayer] = useState("X");
  const [reset, setReset] = useState(false);
  const [count, setCount] = useState([]);
  const arr=[1,2,3,4,5,6,7,8,9];
  let childRef = useRef(null);

  function changePlayer(e) {
    var a=e.target.id
    var arr1=[...count];
    console.log(a,'target')
    setCount([...count,a])
    if(e.target.id ==='*'){
    if (player === "X") {
      setPlayer("O");
    } else {
      setPlayer("X");
    }
  }
  }

  function resetBoard(){
    setReset(true);
  }


  useEffect(()=>{    
    console.log(reset,'ss')
    setReset(false)
    
  },[reset]);

  useEffect(()=>{
    console.log(childRef.current.id,'parent')
  },[player])

  return (
    <div>
      <h1>Player turn:{player}</h1>
      <div className="board" onClick={changePlayer}>
        {arr.map((e,i)=><Move key={i+1} id={i} forwardedRef={childRef} symbol={player} reset={reset} onClick={changePlayer}/>)}
      </div>
      <button onClick={resetBoard}>reset</button>
    </div>
  );
}
