import React, { useState, useEffect } from 'react';
export default function Move(props) {
  const [move, setMove] = useState('');
  function getMove() {
    if (move === '*') {
      setMove(props.symbol);
    }
  }
  useEffect(() => {
    setMove('')
  }, [props.reset])

  return <div className='cell' onClick={getMove}>
    <div id={props.id} value={move}>
      {move}
    </div>
  </div>
}







