import React, { useState, useEffect, useRef } from "react";
import ReactDOM from 'react-dom';
import Move from "./Move";
import { setOriginalNode } from "typescript";

export default function Board(props) {
  const [player, setPlayer] = useState("X");
  const [reset, setReset] = useState(false);
  const [count, setCount] = useState([]);
  const [x, setX] = useState([]);
  const [o, setO] = useState([]);
  const arr = [1, 2, 3, 4, 5, 6, 7, 8, 9];
  var arrayWin = [[1, 2, 3], [4, 5, 6], [7, 8, 9], [1, 3, 5], [3, 5, 7], [1, 4, 7], [2, 5, 8], [3, 6, 9]];

  function changePlayer(e) {
    var a = e.target.id;
    console.log(a)
    setCount([...count, a])
    if (!count.includes(e.target.id)) {
      if (player === "X") {
        setPlayer("O");
        setO([...o, a]);
      } else {
        setPlayer("X");
        setX([...x, a]);
      }
    }
  }

  function resetBoard() {
    setReset(true);
  }

  useEffect(() => {
    setReset(false)
  }, [reset]);

  useEffect(() => {
    console.log(count);
  //  console.log(o,'O');
  //  console.log(x,'X')
    if (count >= 3) { var a = arrayWin.filter(e => { e.every(e => o.includes(e)) }) }

   // console.log('o',a)
  }, [player])

  return (
    <div>
      <h1>Player turn:{player}</h1>
      <div className="board" onClick={changePlayer}>
        {arr.map((e, i) => <Move key={i + 1} id={i} symbol={player} reset={reset} onClick={changePlayer} />)}
      </div>
      <button onClick={resetBoard}>reset</button>
    </div>
  );
}
