import React, { useState, useEffect, useRef } from "react";
import Move from "./Move";

export default function Board(props) {
  const [player, setPlayer] = useState("X");
  const [reset, setReset] = useState(false);
  const move1=useRef();
  move1.display=none;

  function changePlayer() {
    if (player === "X") {
      setPlayer("O");
    } else {
      setPlayer("X");
    }
  }

  function resetBoard(){
    setReset(true);

  }
  useEffect(()=>{
    console.log(reset,'ss')
    setReset(false)
    
  },[reset])
  // function setBoard(){
  //   return for(var i=1;i<10;i++){
  //     <Move key={i} symbol={player} reset={reset}/>
  //   }
  // }
  // function setBoard(){
  //   return for(var i=1;i<10;i++){
  //     <Move key={i} symbol={player} reset={reset}/>
   ////   }
   //// }
  return (
    <div>
      <h1>Player turn:{player}</h1>
      <div className="board" onClick={changePlayer}>
        <Move key='1' list-id='1' ref='move1' symbol={player} reset={reset}/>
        <Move key='2' symbol={player} reset={reset}/>
        <Move key='3' symbol={player} reset={reset}/>
        <Move key='4' symbol={player} reset={reset}/>
        <Move key='5' symbol={player} reset={reset}/>
        <Move key='6' symbol={player} reset={reset}/>
        <Move key='7' symbol={player} reset={reset}/>
        <Move key='8' symbol={player} reset={reset}/>
        <Move key='1' symbol={player} reset={reset}/>
      </div>
      <button onClick={resetBoard}>reset</button>
    </div>
  );
}
