import React, { useState, useEffect, useRef } from "react";
import ReactDOM from 'react-dom';
import Move from "./Move";

export default function Board(props) {
  const [player, setPlayer] = useState("X");
  const [reset, setReset] = useState(false);
  const [count, setCount] = useState(0);
  //const move1=useRef();

  function changePlayer(e) {
   // console.log(e)
    //console.log(typeof e.target.id,e.target.id)
    console.log(e.currentTarget)
    if(e.target.id ==='*'){
      setCount(count=>count+1);
    if (player === "X") {
      setPlayer("O");
    } else {
      setPlayer("X");
    }
  }
  }

  function resetBoard(){
    setReset(true);

  }
  let childRef = useRef(null);
  let ref1 = useRef(null);
  useEffect(()=>{    
    console.log(reset,'ss')
    setReset(false)
    
  },[reset]);

  useEffect(()=>{
    childRef.current.id=count;
    console.log(count)
    console.log(childRef.current.id,'parent')
  },[player])

  return (
    <div>
      <h1>Player turn:{player}</h1>
      <div className="board" onClick={changePlayer}>
        <Move key='1' forwardedRef={childRef} ref={ref1}symbol={player} reset={reset}/>
        <Move key='2' symbol={player} reset={reset}/>
        <Move key='3' symbol={player} reset={reset}/>
        <Move key='4' symbol={player} reset={reset}/>
        <Move key='5' symbol={player} reset={reset}/>
        <Move key='6' symbol={player} reset={reset}/>
        <Move key='7' symbol={player} reset={reset}/>
        <Move key='8' symbol={player} reset={reset}/>
        <Move key='9' symbol={player} reset={reset}/>
      </div>
      <button onClick={resetBoard}>reset</button>
    </div>
  );
}
