import React, { useState, useEffect, useRef } from "react";
import ReactDOM from 'react-dom';
import Move from "./Move";

export default function Board(props) {
  const [player, setPlayer] = useState("X");
  const [reset, setReset] = useState(false);
  const [count, setCount] = useState(0);
  const arr=[1,2,3,4,5,6,7,8,9];

  function changePlayer(e) {
    //console.log(e)
    console.log(e.target.name)
    if(e.target.id ==='*'){
    if (player === "X") {
      setPlayer("O");
    } else {
      setPlayer("X");
    }
  }
  }

  function resetBoard(){
    setReset(true);
  }

  let childRef = useRef(null);

  useEffect(()=>{    
    console.log(reset,'ss')
    setReset(false)
    
  },[reset]);

  useEffect(()=>{
    console.log(childRef.current.id,'parent')
  },[player])

  return (
    <div>
      <h1>Player turn:{player}</h1>
      <div className="board" onClick={changePlayer}>
        {arr.map((e,i)=><Move key={i} id={i} forwardedRef={childRef} symbol={player} reset={reset} onClick={changePlayer}/>)}
      </div>
      <button onClick={resetBoard}>reset</button>
    </div>
  );
}
